import React from 'react';

// Navigation bar component
const NavBar = () => {
  const navBarStyle = {
    backgroundColor: 'blue', // Set the background color to blue
    color: 'white', // Set the text color to white
    padding: '10px', // Add padding for better visual appeal
  };

  return (
    <nav style={navBarStyle}>
      <h1>Player Development App</h1>
      {/* Add your navigation links here */}
    </nav>
  );
};

// Home component
const Home = ({ user }) => {
  const containerStyle = {
    textAlign: 'center',
    backgroundColor: '#f0f0f0',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.2)',
    marginBottom: '20px', // Add margin at the bottom to separate from the navbar
  };

  const headerStyle = {
    color: '#333',
    fontSize: '24px',
    marginBottom: '20px',
  };

  const userStyle = {
    color: 'blue',
  };

  return (
    <div>
      <NavBar /> {/* Include the navigation bar here */}
      <div style={containerStyle}>
        <h2 style={headerStyle}>Welcome to the Player Development App!</h2>
        {user ? (
          <p style={{ ...headerStyle, fontSize: '18px' }}>
            This is the home page of the app. You are signed in as <span style={userStyle}>{user.email}</span>.
          </p>
        ) : (
          <p style={{ ...headerStyle, fontSize: '18px' }}>This is the home page of the app.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
