import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getSkillById, updateSkill } from '../api/api';

const EditSkillForm = () => {
  const { id } = useParams();
  const [formData, setFormData] = useState({
    name: '', // Updated field name to match the schema
    description: '', // Updated field name to match the schema
    difficulty: 'Beginner', // Set a default value as per the schema
  });

  useEffect(() => {
    const fetchSkill = async () => {
      try {
        const response = await getSkillById(id);
        setFormData(response.data);
      } catch (error) {
        console.error('Error fetching skill:', error);
      }
    };

    fetchSkill();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateSkill(id, formData);
      // Handle success, redirect, etc.
    } catch (error) {
      console.error('Error submitting form:', error);
      // Handle error, show error message, etc.
    }
  };

  return (
    <div>
      <h2>Edit Skill</h2>
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} />
        <label>Description:</label>
        <input type="text" name="description" value={formData.description} onChange={handleChange} />
        <label>Difficulty:</label>
        <select
          name="difficulty"
          value={formData.difficulty}
          onChange={(e) => handleChange(e)}
        >
          <option value="Beginner">Beginner</option>
          <option value="Intermediate">Intermediate</option>
          <option value="Advanced">Advanced</option>
        </select>
        <button type="submit">Update Skill</button>
      </form>
    </div>
  );
};

export default EditSkillForm;
