import React from 'react';

const Home = ({ user }) => {
  const containerStyle = {
    textAlign: 'center',
    backgroundColor: '#f0f0f0', // Background color for the container
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.2)', // Box shadow for a card-like effect
  };

  const headerStyle = {
    color: '#333', // Text color for the header
    fontSize: '24px',
  };

  const paragraphStyle = {
    color: '#555', // Text color for the paragraphs
    fontSize: '18px',
  };

  const userStyle = {
    color: 'blue', // Text color for the user's email (if signed in)
  };

  return (
    <div style={containerStyle}>
      <h2 style={headerStyle}>Welcome to the Player Development App!</h2>
      {user ? (
        <p style={paragraphStyle}>
          This is the home page of the app. You are signed in as <span style={userStyle}>{user.email}</span>.
        </p>
      ) : (
        <p style={paragraphStyle}>This is the home page of the app.</p>
      )}
    </div>
  );
};

export default Home;
