import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // Import useHistory
import { createCoach } from '../api/api';

const CoachForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    experience: '',
    team: '',
    coachtype: '', // Updated field name to match the schema
    speciality: '',
  });

  const coachTypes = ['Beginner', 'Intermediate', 'Advanced'];
  const history = useHistory(); // Initialize the history object

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createCoach(formData);
      // After creating the coach, redirect to the coach list
      history.push('/coaches'); // Redirect to the CoachList page
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  return (
    <div>
      <h2>Add Coach</h2>
      <form onSubmit={handleSubmit}>
        {/* ... (rest of the form fields) */}
        <button type="submit">Add Coach</button>
      </form>
    </div>
  );
};

export default CoachForm;
