import React, { useState } from 'react';
import { createCoach } from '../api/api';


const CoachForm = () => {
    const [formData, setFormData] = useState({
      name: '',
      experience: '',
      team: '',
      speciality: '',
    });

    const coachTypes = ['Beginner', 'Intermediate', 'Advanced']; 

    const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
      e.preventDefault();
      try {
        await createCoach(formData);
      } catch (error) {
        console.error('Error submitting form:', error);
      }
    };

    return (
      <div>
        <h2>Add Coach</h2>
        <form onSubmit={handleSubmit}>
          <label>Name:</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} />
          <label>Coach Type:</label>
          <select name="coachType" id="coaches" value={formData.coachType} onChange={(e) => handleChange(e)}>
            {coachTypes.map((coach, i) => <option key={i} value={coach}>{coach}</option>)}
          </select>
          {/* make this input a select with options. The options are set above  coachTypes */}
          {/* <input type="text" name="coachType" value={formData.coachType} onChange={handleChange} /> */}
          <label>Best Used For:</label>
          <input type="text" name="speciality" value={formData.speciality} onChange={handleChange} />
          <button type="submit">Add Coach</button>
        </form>
      </div>
    );
  };

  export default CoachForm;
